#include <stdio.h>
int main()
{//========================================================================================> Scope 1==========> |
    int x = 1;                                                              //  x=1                             |          
    int y = 2;                                                              //  y=2                             |          
    int z = 3;                                                              //  z=3                             |          
    printf("(x,y,z) = (%d,%d,%d)\n", x, y, z);  // 1, 2, 3                  //                                  |          
    {//===========================================================================> Scope 2 ========> |         |
        int x = 100;                                                        //  x=100                 | x=1     |      
        y = 2;                                                              //  y=2                   | y=2     |
        if (x > y) { // scope 2.1                                           //                        |         |
            z = x;                                                          //  z=100                 | z=100   |
        } else {    // scope 2.2                                            //  x>y immer             |         |
            z = y;                                                          //                        |         |
        }                                                                   //                        |         |
        printf("(x,y,z) = (%d,%d,%d)\n", x, y, z);  // 100, 2, 100          //                        |         |
        {//==================================================================>==== Scope 3 ===>|      |         |
            int z = y;                                                      //  z=2            | z=100| z=100   |
            y = 200;                                                        //  y=200          | y=200| y=200   |
            printf("(x,y,z) = (%d,%d,%d)\n", x, y, z);  // 100, 200, 2      //                 |      |         |
        }//<================================================================= ====Scope 3 end <|      |         |
        printf("(x,y,z) = (%d,%d,%d)\n", x, y, z);  // 100, 200, 100        //                        |         |
    }//<===================================================================== ====Scope 2 end <====== |         |
    printf("(x,y,z) = (%d,%d,%d)\n", x, y, z);  // 1, 200, 100              //                                  |
}//<========================================================================================Scope 1 end <====== |